import { useState } from "react";
import "./App.css";

function App() {
  
  const [count,setCount] = useState(0);
  function decreaseHandler(){
    setCount(count-1);
  }
  return (
    <div className=" w-[100vw] h-[100vh] flex justify-center items-center flex-col bg-[#344151] gap-10">
      <div className=" text-2xl font-bold text-blue-400">Increment & Decrement</div>
      <div className=" flex bg-white justify-center items-center gap-12 py-3">
        <button onClick={decreaseHandler} className=" text-[50px] ">
          -
        </button>
        <div>
            {count}
        </div>
        <button>
          +
        </button>
      </div>
      <button>
        Reset
      </button>
    </div>
  );
}

export default App;
